// Validate incoming deployment request
const payload = msg.payload;

if (!payload.fleetName || !payload.sourceDirectory) {
    msg.statusCode = 400;
    msg.payload = { error: 'Missing required fields: fleetName, sourceDirectory' };
    return [null, msg]; // Send to error output
}

// Prepare deployment context
msg.deployment = {
    id: Date.now().toString(),
    fleetName: payload.fleetName,
    sourceDirectory: payload.sourceDirectory,
    branch: payload.branch || 'main',
    commitHash: payload.commitHash || 'latest',
    environment: payload.environment || 'production',
    startTime: new Date().toISOString()
};

msg.payload = {
    message: 'Deployment validation successful',
    deployment: msg.deployment
};

return [msg, null]; // Send to success output

// Perform pre-deployment checks
const deployment = msg.deployment;

// Check fleet exists and get current status
msg.fleetName = deployment.fleetName;
msg.payload = {
    deployment: deployment,
    step: 'pre-deploy-checks',
    message: 'Checking fleet status and prerequisites'
};

return msg;

const fleetInfo = msg.payload;
const deployment = msg.deployment;

// Analyze fleet health before deployment
const deviceCount = fleetInfo.owns__device?.length || 0;
const onlineDevices = fleetInfo.owns__device?.filter(d => d.is_online)?.length || 0;
const healthPercentage = deviceCount > 0 ? (onlineDevices / deviceCount) * 100 : 0;

deployment.preDeployStatus = {
    totalDevices: deviceCount,
    onlineDevices: onlineDevices,
    healthPercentage: healthPercentage,
    readyForDeployment: healthPercentage >= 50 // At least 50% online
};

if (!deployment.preDeployStatus.readyForDeployment) {
    msg.error = `Fleet health too low: ${healthPercentage}% online`;
    return [null, msg]; // Send to error path
}

msg.deployment = deployment;
msg.payload = {
    deployment: deployment,
    step: 'fleet-analysis',
    message: `Fleet healthy: ${healthPercentage}% devices online`
};

return [msg, null]; // Send to success path

const releases = msg.payload;
const deployment = msg.deployment;

// Store current release for potential rollback
if (releases && releases.length > 0) {
    deployment.rollbackRelease = {
        id: releases[0].id,
        commit: releases[0].commit,
        version: releases[0].release_version
    };
}

msg.deployment = deployment;
msg.payload = {
    deployment: deployment,
    step: 'rollback-preparation',
    message: 'Rollback information stored'
};

return msg;

const deployment = msg.deployment;

// Note: This would use balena-deploy node in a real scenario
// For now, we'll simulate the deployment process
msg.payload = {
    operation: 'push',
    fleetName: deployment.fleetName,
    sourceDirectory: deployment.sourceDirectory,
    nocache: true,
    deploymentId: deployment.id
};

msg.deployment = deployment;

return msg;

// Simulate deployment process
// In real scenario, this would be a balena-deploy node
const deployment = msg.deployment;

// Simulate deployment success/failure (90% success rate)
const deploymentSuccess = Math.random() > 0.1;

deployment.deploymentResult = {
    success: deploymentSuccess,
    endTime: new Date().toISOString(),
    duration: Date.now() - new Date(deployment.startTime).getTime()
};

if (deploymentSuccess) {
    deployment.newReleaseId = 'release_' + Date.now();
    msg.payload = {
        deployment: deployment,
        step: 'deployment-complete',
        message: 'Code deployed successfully'
    };
    return [msg, null];
} else {
    msg.error = 'Deployment failed during build process';
    msg.deployment = deployment;
    return [null, msg];
}

const devices = msg.payload;
const deployment = msg.deployment;

// Filter devices for this fleet
const fleetDevices = devices.filter(d => 
    d.belongs_to__application && 
    d.belongs_to__application[0].app_name === deployment.fleetName
);

const totalDevices = fleetDevices.length;
const onlineDevices = fleetDevices.filter(d => d.is_online).length;
const healthPercentage = totalDevices > 0 ? (onlineDevices / totalDevices) * 100 : 0;

deployment.postDeployStatus = {
    totalDevices: totalDevices,
    onlineDevices: onlineDevices,
    healthPercentage: healthPercentage,
    healthImprovement: healthPercentage - deployment.preDeployStatus.healthPercentage
};

// Determine if deployment was successful
const deploymentSuccess = healthPercentage >= 75; // Expect 75% or better post-deploy

if (deploymentSuccess) {
    msg.payload = {
        deployment: deployment,
        step: 'deployment-verified',
        message: `Deployment successful: ${healthPercentage}% devices healthy`
    };
    return [msg, null];
} else {
    msg.error = `Post-deployment health check failed: ${healthPercentage}% healthy`;
    msg.deployment = deployment;
    return [null, msg];
}

const deployment = msg.deployment;

// Create success response
msg.statusCode = 200;
msg.payload = {
    success: true,
    deploymentId: deployment.id,
    fleetName: deployment.fleetName,
    duration: deployment.deploymentResult.duration,
    healthStatus: deployment.postDeployStatus,
    message: 'Deployment completed successfully'
};

return msg;

const deployment = msg.deployment;

if (!deployment.rollbackRelease) {
    msg.error = 'No rollback release available';
    return [null, msg];
}

msg.payload = {
    fleetName: deployment.fleetName,
    releaseId: deployment.rollbackRelease.id
};

msg.deployment = deployment;

return [msg, null];

const deployment = msg.deployment;

msg.statusCode = 206; // Partial Content - deployment rolled back
msg.payload = {
    success: false,
    rollback: true,
    deploymentId: deployment.id,
    fleetName: deployment.fleetName,
    rolledBackTo: deployment.rollbackRelease,
    message: 'Deployment failed and was rolled back'
};

return msg;

const deployment = msg.deployment || {};
const error = msg.error || 'Unknown deployment error';

msg.statusCode = 500;
msg.payload = {
    success: false,
    deploymentId: deployment.id,
    fleetName: deployment.fleetName,
    error: error,
    message: 'Deployment failed'
};

return msg;

const result = msg.payload;
const isSuccess = result.success;
const isRollback = result.rollback;

let message;
let emoji;

if (isSuccess) {
    emoji = '‚úÖ';
    message = `Deployment ${result.deploymentId} to ${result.fleetName} completed successfully in ${Math.round(result.duration/1000)}s`;
} else if (isRollback) {
    emoji = 'üîÑ';
    message = `Deployment ${result.deploymentId} to ${result.fleetName} failed and was rolled back`;
} else {
    emoji = '‚ùå';
    message = `Deployment ${result.deploymentId} to ${result.fleetName} failed: ${result.error}`;
}

msg.payload = {
    notification: {
        title: `${emoji} Balena Deployment ${isSuccess ? 'Success' : 'Failed'}`,
        message: message,
        timestamp: new Date().toISOString(),
        details: result
    }
};

return msg;
